// global declarations.
const int spawn_time=28;// vehicles are spawned every 'spawn_time' time units. A single time unit is equal to 'step' seconds. 
                         // For example if a step = 0.02 seconds , and spawn_time= 14 time units, then one vehicle is spawned every 14*0.02 = 0.28 seconds  
const int no_of_lanes=3; 

const double step= 0.02;    // time step in seconds
const double speed_limit = 25.0; //Road speed limit in meters  
const double max_distance_to_stop_before_intersection = speed_limit * step; // a vehicle stopping at the intersection will stop at a distance(meters)
                                                                            // ranging from zero to this value before the intersection enterance  
int Trafficlightdir; // indicates which traffic signal direction is now on, 0:North , 1:East , 2:South , 3:West. 
//when a traffic light signal is on, that means this direction could have green,yellow or red signal depending on the 'Trafficlightcol' while all other directions are red
int Trafficlightcol; //Traffic light colour, 0:green, 1:yellow, 2:red

int vehicleID; //unique ID for each vehicle spawned, given incrementaly starting from 1.
bool terminatedvehicleID[10000];
int spawn_road[10000]; //starting road ID => 0:North , 1:East , 2:South , 3:West
int spawn_lane[10000]; //starting lane ID => we start counting lanes from North road then East then South then West 
                      //                    each road we start counting from the middle intersecting axes of the map
                      //                    we start by the first lane on the North road giving it a spawn_lane id of 0  
                     //                    and continue in an incremental order as shown below for the case of 3 lanes


//                                  8 7 6 | 0 1 2                                            
//                               11              11           
//                               10              10    
//                               9               9
//                               --              --
//                               3               3
//                               4               4
//                               5               5 
//                                 8 7 6 | 0 1 2                                              
//  

int dest_road[10000]; // destination road ID => 0:North , 1:East , 2:South , 3:West
int dest_lane[10000]; // destination lane ID
int current_road[10000];
int current_lane[10000];

const double PI=3.141592654;
                                                      
double spawn_pointX[10000]; // x coordinates at which the middle front point of the vehicle is spawned at
double spawn_pointY[10000]; // y coordinates at which the middle front point of the vehicle is spawned at

double positionUL[10000][2]; // X([id][0]) and Y([id][1]) coordinates of the Upper Left corner point of the vehicle 
double positionUR[10000][2]; // X([id][0]) and Y([id][1]) coordinates of the Upper Left corner point of the vehicle 
double positionLL[10000][2]; // X([id][0]) and Y([id][1]) coordinates of the Upper Left corner point of the vehicle 
double positionLR[10000][2]; // X([id][0]) and Y([id][1]) coordinates of the Upper Left corner point of the vehicle 
double positionLM[10000][2]; //X([id][0]) and Y([id][1]) coordinates of the Lower Middle corner point of the vehicle
double positionUM[10000][2];

double velocity[10000]; // meters/sec
double acceleration[10000]; // meters/sec^2
double distance[10000]; // meters
double distance_to_intersection[10000]; //meters
double distance_to_come_to_complete_stop[10000]; //meters
double perpendicular_distance_to_end_of_journey[10000]; //meters
double total_distance_traveled[10000];
int crossing_state[10000]; // vehicle crossing states, 0:prior to crossing, 1:crossing, 2: after crossing
bool stopping_before_int[10000]; 

//turning variables
double orientation[10000]; // measured from the x-axis in an anti-clockwise direction 
double steeringangle[10000]; //measured from the orientation of the vehicle in an anti-clockwise direction
double final_orientation[10000];
bool once[10000];


int no_of_collisions; // counter for the total number of collisions
int no_of_collisions_on_intersection;// counter for the number of collisions happening within the intersection area
int non_same_lane_collision_counter; // counting collisions that happen between vehicles that are not on the same lane
bool pilledup; // flag to signal vehicles piling up on any of the roads reaching the start of a road
int id_of_first_vehicle_pilling_up; 
int no_of_cleared_vehicles; // a counter for the number of vehicles which have finished their journey
int no_of_vehicles_crossed; // a counter for the number of vehicles which have crossed the intersection
double average_trip_delay; // for all vehicles which have finished their journey, the trip delay is the time taken by vehicle to finish its journey subtracted from the time it takes it to finish its journey if the vehicle didn't stop at the intersection(going with a constant speed_limit) 
double total_journey_time[10000];

//variables for determining vehicle infront
int last_vehicle_on_spawn_lane_id[12]; //  for each lane we keep track of the last vehicle entering the lane at each time step   
int last_vehicle_on_dest_lane_id[12];
int vehicle_infront_id[10000]; // id of the vehicle infront of this vehicle
double distance_to_vehicle_infront[10000];

const double safety_distance=3.0; //vertical safety distance to keep between vehicles on the road  

struct{
   int  id1[10000];
   int  id2[10000];
}collidedvehicles; // structure that stores the ids of collided vehicles so that we count a collision between 2 vehicles once

struct{
   int  id1[10000];
   int  id2[10000];
}nonsamelanecollidedvehicles; // structure that stores the ids of collided vehicles not on same lane so that we count a collision between them once

//grid dimensions
const double lane_width=3.0;
const double intersection_length= no_of_lanes * lane_width * 2; //meters
const double road_length= (318.0-intersection_length)/2;  //meters //a road can either end at an intersection or start at one


//vehicle characteristics 
// charactaristics matches the charactaristics of a small size vehicle having moderate performance
const double vehicle_length= 4.0; //meters //5.0
const double vehicle_width= 1.0; //meters //1.85
const double vehicle_maxAcceleration=3.25; //meters/s^2
const double vehicle_maxDeceleration=-39.0; //meters/s^2

// dynamically created template
dynamic Vehicle(const int id); 



// choose the spawn lane based on the direction the vehicle wants to take, and set the destination road and lane
void chooseSpawnLane(int id,int dir){
// dir : 1 left , 2 straight, 3 right
if(no_of_lanes==3){
if (spawn_road[id]==0){
if(dir ==1){
spawn_lane[id]=0;
spawn_pointX[id]=road_length+ 3.5 * (0.5 * intersection_length/no_of_lanes) ;
spawn_pointY[id]=0;
dest_road[id]=3;
dest_lane[id]=9;
}else if(dir ==2){
spawn_lane[id]=1;
spawn_pointX[id]=road_length+ 4.5 * (0.5 * intersection_length/no_of_lanes) ;
spawn_pointY[id]=0;
dest_road[id]=spawn_road[id];
dest_lane[id]=spawn_lane[id];
}else if(dir ==3){
spawn_lane[id]=2;
spawn_pointX[id]=road_length+ 5.5 * (0.5 * intersection_length/no_of_lanes) ;
spawn_pointY[id]=0;
dest_road[id]=1;
dest_lane[id]=5;
}
}else if (spawn_road[id]==1){
if(dir ==1){
spawn_lane[id]=3;
spawn_pointX[id]=0;
spawn_pointY[id]=road_length+ 2.5 * (0.5 * intersection_length/no_of_lanes);
dest_road[id]=0;
dest_lane[id]=0;
}else if(dir ==2){
spawn_lane[id]=4;
spawn_pointX[id]=0;
spawn_pointY[id]=road_length+ 1.5 * (0.5 * intersection_length/no_of_lanes);
dest_road[id]=spawn_road[id];
dest_lane[id]=spawn_lane[id];
}else if(dir ==3){
spawn_lane[id]=5;
spawn_pointX[id]=0;
spawn_pointY[id]=road_length+ 0.5 * (0.5 * intersection_length/no_of_lanes);
dest_road[id]=2;
dest_lane[id]=8;
}
}else if (spawn_road[id]==2){
if(dir ==1){
spawn_lane[id]=6;
spawn_pointX[id]=road_length+ 2.5 * (0.5 * intersection_length/no_of_lanes) ;
spawn_pointY[id]=2*road_length + intersection_length;
dest_road[id]=1;
dest_lane[id]=3;
}else if(dir ==2){
spawn_lane[id]=7;
spawn_pointX[id]=road_length+ 1.5 * (0.5 * intersection_length/no_of_lanes) ;
spawn_pointY[id]=2*road_length + intersection_length;
dest_road[id]=spawn_road[id];
dest_lane[id]=spawn_lane[id];
}else if(dir ==3){
spawn_lane[id]=8;
spawn_pointX[id]=road_length+ 0.5 * (0.5 * intersection_length/no_of_lanes);
spawn_pointY[id]=2*road_length + intersection_length;
dest_road[id]=3;
dest_lane[id]=11;
}
}else if(spawn_road[id]==3){
if(dir ==1){
spawn_lane[id]=9;
spawn_pointX[id]=2*road_length + intersection_length;
spawn_pointY[id]=road_length+ 3.5 * (0.5 * intersection_length/no_of_lanes) ;
dest_road[id]=2;
dest_lane[id]=6;
}else if(dir ==2){
spawn_lane[id]=10;
spawn_pointX[id]= 2*road_length + intersection_length; 
spawn_pointY[id]= road_length+ 4.5 * (0.5 * intersection_length/no_of_lanes) ;
dest_road[id]=spawn_road[id];
dest_lane[id]=spawn_lane[id];
}else if(dir ==3){
spawn_lane[id]=11;
spawn_pointX[id]= 2*road_length + intersection_length; 
spawn_pointY[id]= road_length+ 5.5 * (0.5 * intersection_length/no_of_lanes) ;
dest_road[id]=0;
dest_lane[id]=2;
}
}

}else if(no_of_lanes==2){
// if number of lanes is 2 then vehicles going straight have to take the left lane, i.e. right lanes are only for right turns

if (spawn_road[id]==0){
if(dir ==1){
spawn_lane[id]=0;
spawn_pointX[id]=road_length+ 2.5 * (0.5 * intersection_length/no_of_lanes) ;
spawn_pointY[id]=0;
dest_road[id]=3;
dest_lane[id]=6;
}else if(dir ==2){
spawn_lane[id]=0;
spawn_pointX[id]=road_length+ 2.5 * (0.5 * intersection_length/no_of_lanes) ;
spawn_pointY[id]=0;
dest_road[id]=spawn_road[id];
dest_lane[id]=spawn_lane[id];
}else if(dir ==3){
spawn_lane[id]=1;
spawn_pointX[id]=road_length+ 3.5 * (0.5 * intersection_length/no_of_lanes) ;
spawn_pointY[id]=0;
dest_road[id]=1;
dest_lane[id]=3;
}
}else if (spawn_road[id]==1){
if(dir ==1){
spawn_lane[id]=2;
spawn_pointX[id]=0;
spawn_pointY[id]=road_length+ 1.5 * (0.5 * intersection_length/no_of_lanes);
dest_road[id]=0;
dest_lane[id]=0;
}else if(dir ==2){
spawn_lane[id]=2;
spawn_pointX[id]=0;
spawn_pointY[id]=road_length+ 1.5 * (0.5 * intersection_length/no_of_lanes);
dest_road[id]=spawn_road[id];
dest_lane[id]=spawn_lane[id];
}else if(dir ==3){
spawn_lane[id]=3;
spawn_pointX[id]=0;
spawn_pointY[id]=road_length+ 0.5 * (0.5 * intersection_length/no_of_lanes);
dest_road[id]=2;
dest_lane[id]=5;
}
}else if (spawn_road[id]==2){
if(dir ==1){
spawn_lane[id]=4;
spawn_pointX[id]=road_length+ 1.5 * (0.5 * intersection_length/no_of_lanes) ;
spawn_pointY[id]=2*road_length + intersection_length;
dest_road[id]=1;
dest_lane[id]=2;
}else if(dir ==2 ){
spawn_lane[id]=4;
spawn_pointX[id]=road_length+ 1.5 * (0.5 * intersection_length/no_of_lanes) ;
spawn_pointY[id]=2*road_length + intersection_length;
dest_road[id]=spawn_road[id];
dest_lane[id]=spawn_lane[id];
}else if(dir ==3){
spawn_lane[id]=5;
spawn_pointX[id]=road_length+ 0.5 * (0.5 * intersection_length/no_of_lanes) ;
spawn_pointY[id]=2*road_length + intersection_length;
dest_road[id]=3;
dest_lane[id]=7;
}
}else if(spawn_road[id]==3){
if(dir ==1){
spawn_lane[id]=6;
spawn_pointX[id]=2*road_length + intersection_length;
spawn_pointY[id]=road_length+ 2.5 * (0.5 * intersection_length/no_of_lanes) ;
dest_road[id]=2;
dest_lane[id]=4;
}else if(dir ==2){
spawn_lane[id]=6;
spawn_pointX[id]=2*road_length + intersection_length;
spawn_pointY[id]=road_length+ 2.5 * (0.5 * intersection_length/no_of_lanes) ;
dest_road[id]=spawn_road[id];
dest_lane[id]=spawn_lane[id];
}else if(dir ==3){
spawn_lane[id]=7;
spawn_pointX[id]=2*road_length + intersection_length;
spawn_pointY[id]=road_length+ 3.5 * (0.5 * intersection_length/no_of_lanes) ;
dest_road[id]=0;
dest_lane[id]=1;
}
}

}else if(no_of_lanes==1){
// all directions on a single road go through the same lane
spawn_lane[id]= spawn_road[id];

if (spawn_road[id]==0){
spawn_pointX[id]=road_length+ 1.5 * (0.5 * intersection_length/no_of_lanes) ;
spawn_pointY[id]=0;
if(dir ==1)
dest_road[id]=3;
else if(dir ==2)
dest_road[id]=spawn_road[id];
else if(dir ==3)
dest_road[id]=1;
}else if (spawn_road[id]==1){
spawn_pointX[id]=0;
spawn_pointY[id]=road_length+ 0.5 * (0.5 * intersection_length/no_of_lanes);
if(dir ==1)
dest_road[id]=0;
else if(dir ==2)
dest_road[id]=spawn_road[id];
else if(dir ==3)
dest_road[id]=2;
}else if (spawn_road[id]==2){
spawn_pointX[id]=road_length+ 0.5 * (0.5 * intersection_length/no_of_lanes) ;
spawn_pointY[id]=2*road_length + intersection_length;
if(dir ==1)
dest_road[id]=1;
else if(dir ==2)
dest_road[id]=spawn_road[id];
else if(dir ==3)
dest_road[id]=3;
}else if(spawn_road[id]==3){
spawn_pointX[id]=2*road_length + intersection_length;
spawn_pointY[id]=road_length+ 1.5 * (0.5 * intersection_length/no_of_lanes) ;
if(dir ==1)
dest_road[id]=2;
else if(dir ==2)
dest_road[id]=spawn_road[id];
else if(dir ==3)
dest_road[id]=0;
}
dest_lane[id]=dest_road[id];
}

}

//set vehicle's initial state
void setVehInitialState(int id){

//initialize vehicle's velocity and acceleration
//vehicles are spawned with a constant velocity according to the road's speed limit 
velocity[id]=speed_limit; 
acceleration[id]=0.0; 

vehicle_infront_id[id]=last_vehicle_on_spawn_lane_id[spawn_lane[id]];    
last_vehicle_on_spawn_lane_id[spawn_lane[id]]=id;

//set vehicle's position at its spawn position at the start of the vehicle's movement
if(spawn_road[id]==0){
positionLM[id][0] = spawn_pointX[id];
positionLM[id][1] = spawn_pointY[id] - vehicle_length;
positionUL[id][0] = spawn_pointX[id] - vehicle_width/2;
positionUL[id][1] = spawn_pointY[id];
positionUR[id][0] = spawn_pointX[id] + vehicle_width/2;
positionUR[id][1] = spawn_pointY[id];
positionLL[id][0] = spawn_pointX[id] - vehicle_width/2;
positionLL[id][1] = spawn_pointY[id] - vehicle_length;
positionLR[id][0] = spawn_pointX[id] + vehicle_width/2;
positionLR[id][1] = spawn_pointY[id] - vehicle_length;
}else if(spawn_road[id]==1){
positionLM[id][0] = spawn_pointX[id] - vehicle_length;
positionLM[id][1] = spawn_pointY[id];
positionUL[id][0] = spawn_pointX[id];
positionUL[id][1] = spawn_pointY[id] + vehicle_width/2;
positionUR[id][0] = spawn_pointX[id];
positionUR[id][1] = spawn_pointY[id] - vehicle_width/2;
positionLL[id][0] = spawn_pointX[id] - vehicle_length;
positionLL[id][1] = spawn_pointY[id] + vehicle_width/2;
positionLR[id][0] = spawn_pointX[id] - vehicle_length;
positionLR[id][1] = spawn_pointY[id] - vehicle_width/2;
}else if(spawn_road[id]==2){
positionLM[id][0] = spawn_pointX[id];
positionLM[id][1] = spawn_pointY[id] + vehicle_length;
positionUL[id][0] = spawn_pointX[id] + vehicle_width/2;
positionUL[id][1] = spawn_pointY[id];
positionUR[id][0] = spawn_pointX[id] - vehicle_width/2;
positionUR[id][1] = spawn_pointY[id];
positionLL[id][0] = spawn_pointX[id] + vehicle_width/2;
positionLL[id][1] = spawn_pointY[id] + vehicle_length;
positionLR[id][0] = spawn_pointX[id] - vehicle_width/2;
positionLR[id][1] = spawn_pointY[id] + vehicle_length;
}else if(spawn_road[id]==3){
positionLM[id][0] = spawn_pointX[id] + vehicle_length;
positionLM[id][1] = spawn_pointY[id];
positionUL[id][0] = spawn_pointX[id];
positionUL[id][1] = spawn_pointY[id] - vehicle_width/2;
positionUR[id][0] = spawn_pointX[id];
positionUR[id][1] = spawn_pointY[id] + vehicle_width/2;
positionLL[id][0] = spawn_pointX[id]+ vehicle_length;
positionLL[id][1] = spawn_pointY[id] - vehicle_width/2;
positionLR[id][0] = spawn_pointX[id]+ vehicle_length;
positionLR[id][1] = spawn_pointY[id] + vehicle_width/2;
}

// set vehicle's current road and lane 
current_road[id]=spawn_road[id];
current_lane[id]=spawn_lane[id];

//set vehicle's orientation
if(spawn_road[id]==0)
orientation[id]=PI/2;
else if(spawn_road[id]==1)
orientation[id]=0.0; 
else if(spawn_road[id]==2)
orientation[id]=3*PI/2; 
else if(spawn_road[id]==3)
orientation[id]=PI; 

//mark vehicle's final orientation
if(dest_road[id]==0)
final_orientation[id]=PI/2;
else if(dest_road[id]==1)
final_orientation[id]=0.0; 
else if(dest_road[id]==2)
final_orientation[id]=3*PI/2; 
else if(dest_road[id]==3)
final_orientation[id]=PI;
}


//If the vehicle is moving with a lower speed that the speed limit , set it's acceleration to maximum value until reaching the speed limit 
void setVehicleAccelerationForNormalMode(int id){
if(velocity[id]<speed_limit){
acceleration[id]=vehicle_maxAcceleration;
}
else acceleration[id]=0.0; // keep with constant speed
}

void setVehicleAccelerationForStoppingMode(int id){
if(velocity[id] > 0.0){
acceleration[id]=vehicle_maxDeceleration;
}
else {acceleration[id]=0.0; velocity[id]=0.0;} // keep acceleration and velocity values to zero in order not to move backwards
}

void TrafficLightPolicy(int id){

//At any time in any location each vehicle should stop for vehicle infront if there is a vehicle infront and it didn't reach the end of its journey, and distance to it is less than distance to come to a complete stop + a safety distance 
if(terminatedvehicleID[vehicle_infront_id[id]]==false && vehicle_infront_id[id]!=0){ 
if(distance_to_vehicle_infront[id] - safety_distance <= distance_to_come_to_complete_stop[id]){
setVehicleAccelerationForStoppingMode(id);
return;
}
}

if(crossing_state[id]==0) {//vehicle didn't reach the intersection yet
//vehicle takes a decision to start decelerating in order to stop before the intersection when the distance remaining to the intersection is less than or equal to the distance
// the vehicle takes to come to a complete stop (we add a constant max_distance_to_stop_before_intersection to make sure the vehicle stops at or before the intersection enterance)
// and when the traffic light signal is not green i.e. yellow or red
if((distance_to_intersection[id] - max_distance_to_stop_before_intersection <= distance_to_come_to_complete_stop[id]) && !(Trafficlightdir==spawn_road[id] && Trafficlightcol==0)){
// if vehicle is not able to stop before the intersection enterance due to its speed it will cross the intersection no matter what the traffic light signal is  
// If the vehicle takes decision to stop it sets the stopping boolean variable to true and will keep in the stopping mode (until traffic light signal becomes green)
if((distance_to_intersection[id] <= distance_to_come_to_complete_stop[id]) && (stopping_before_int[id]!= true)){
setVehicleAccelerationForNormalMode(id);
}else{
setVehicleAccelerationForStoppingMode(id);
stopping_before_int[id]=true;
}
// else do nothing i.e. vehicle is already decelerating to stop at the intersection.
}else {setVehicleAccelerationForNormalMode(id);stopping_before_int[id]=false;}

}else if(crossing_state[id]==1) { 
setVehicleAccelerationForNormalMode(id);
}else if(crossing_state[id]==2){
setVehicleAccelerationForNormalMode(id);
}

}

void steering(double steeringangle, double distance,double vehicle_length, double x, double y, double heading,int id){	

			    double beta =  (distance/vehicle_length) * (sin(steeringangle)/cos(steeringangle));
			
                
			    if (beta < 0.01){
    		        x = x + distance*cos(heading);
			        y = y + distance*sin(heading);
			        heading = (heading + beta) ;//% (2 * PI);///////////////////////////////////////
			        if(heading> 2 * PI)
                    heading = heading- 2 * PI;
                    else if(heading < 0.0)
                    heading = 2 * PI + heading;
                    }
			    else{
                    double Radius = distance/beta;
			        x = x - sin(heading)*Radius + sin(heading+beta)* Radius;
			        y = y + cos(heading)*Radius - cos(heading+beta)* Radius;
			        heading = (heading + beta);
			        if(heading> 2 * PI)
                    heading = heading- 2 * PI;
                    else if(heading < 0.0)
                    heading = 2 * PI + heading;
                    }
                                
                positionLM[id][0]=x;
                positionLM[id][1]=y;
                orientation[id]= heading;  
}

// choose which policy to run, if no policy was chosen then vehicles will be moving freely without any policy to guide them while traversing the intersection, and collisions are inevitable then
void runPolicy(int id){
// Traffic light policy
TrafficLightPolicy(id);
}

double getsteeringangle(double sa, int ID){
bool turning=false;
sa=0.0;

if(orientation[ID] == final_orientation[ID])
return sa;

//IF VEHICLE IS TURNING then  2m of the front of the vehicle has to be in the intersection before the vehicle can start turning
if(no_of_lanes==3){
if(spawn_lane[ID]==0)
if( positionUM[ID][1] -2 >=road_length)
turning=true;
if(spawn_lane[ID]==2)
if( positionUM[ID][1] -2 >=road_length )
turning=true;
if(spawn_lane[ID]==3)
if( positionUM[ID][0] -2 >=road_length)
turning=true;
if(spawn_lane[ID]==5)
if( positionUM[ID][0] -2 >=road_length )
turning=true;
if(spawn_lane[ID]==6)
if( positionUM[ID][1] +2 <= road_length+intersection_length && positionUM[ID][1]>0)
turning=true;
if(spawn_lane[ID]==8)
if( positionUM[ID][1] +2 <= road_length+intersection_length && positionUM[ID][1]>0 )
turning=true;
if(spawn_lane[ID]==9)
if( positionUM[ID][0] +2 <= road_length+intersection_length && positionUM[ID][0]>0)
turning=true;
if(spawn_lane[ID]==11)
if( positionUM[ID][0] +2 <= road_length+intersection_length && positionUM[ID][0]>0)
turning=true;
}else if(no_of_lanes==2){
if(spawn_lane[ID]==0)
if( positionUM[ID][1] -2 >=road_length)
turning=true;
if(spawn_lane[ID]==1)
if( positionUM[ID][1] -2 >=road_length)
turning=true;
if(spawn_lane[ID]==2)
if( positionUM[ID][0] -2 >=road_length)
turning=true;
if(spawn_lane[ID]==3)
if( positionUM[ID][0] -2 >=road_length)
turning=true;
if(spawn_lane[ID]==4)
if( positionUM[ID][1] +2 <= road_length+intersection_length && positionUM[ID][1]>0)
turning=true;
if(spawn_lane[ID]==5)
if( positionUM[ID][1] +2 <= road_length+intersection_length && positionUM[ID][1]>0)
turning=true;
if(spawn_lane[ID]==6)
if( positionUM[ID][0] +2 <= road_length+intersection_length && positionUM[ID][0]>0)
turning=true;
if(spawn_lane[ID]==7)
if( positionUM[ID][0] +2 <= road_length+intersection_length && positionUM[ID][0]>0)
turning=true;
}else if(no_of_lanes==1){
//N-W
if(spawn_lane[ID]==0 && dest_lane[ID]==3)
if( positionUM[ID][1] -2 >=road_length)
turning=true;
//N-E
if(spawn_lane[ID]==0  && dest_lane[ID]==1)
if( positionUM[ID][1] -2 >=road_length)
turning=true;
//E-N
if(spawn_lane[ID]==1 && dest_lane[ID]==0)
if( positionUM[ID][0] -2 >=road_length)
turning=true;
//E-S
if(spawn_lane[ID]==1 && dest_lane[ID]==2)
if( positionUM[ID][0] -2 >=road_length)
turning=true;
//S-E
if(spawn_lane[ID]==2 && dest_lane[ID]==1)
if( positionUM[ID][1] +2 <= road_length+intersection_length && positionUM[ID][1]>0)
turning=true;
//S-W
if(spawn_lane[ID]==2 && dest_lane[ID]==3)
if( positionUM[ID][1] +2 <= road_length+intersection_length && positionUM[ID][1]>0)
turning=true;
//W-S
if(spawn_lane[ID]==3 && dest_lane[ID]==2)
if( positionUM[ID][0] +2 <= road_length+intersection_length && positionUM[ID][0]>0)
turning=true;
//W-N
if(spawn_lane[ID]==3 && dest_lane[ID]==0)
if( positionUM[ID][0] +2 <= road_length+intersection_length && positionUM[ID][0]>0)
turning=true;
}

if(turning==true){
// To make this modular we should use tan inverse function, but as it is not included in uppaal library, so the steering angle (sa) here should be altered if the vehicle size and/or intersection length are changed.
if(no_of_lanes==3){
if(spawn_lane[ID]==0 || spawn_lane[ID]==3 || spawn_lane[ID]== 6 || spawn_lane[ID]==9)
//sa=0.27;
sa=0.31;
else if(spawn_lane[ID]==2 || spawn_lane[ID]==5 || spawn_lane[ID]== 8 || spawn_lane[ID]==11)
//sa=-0.7204150817;
sa=-1.0;
} else if(no_of_lanes==2){
if(spawn_lane[ID]==0 || spawn_lane[ID]==2 || spawn_lane[ID]== 4 || spawn_lane[ID]==6)
//sa=0.33;
sa=0.4;
else if(spawn_lane[ID]==1 || spawn_lane[ID]==3 || spawn_lane[ID]== 5 || spawn_lane[ID]==7)
//sa=-0.7204150817;
sa=-1.1;
}else if(no_of_lanes==1){
if((spawn_lane[ID]==0 && dest_lane[ID]==3) || (spawn_lane[ID]==1 && dest_lane[ID]==0) || spawn_lane[ID]==2 && dest_lane[ID]==1 || spawn_lane[ID]==3 && dest_lane[ID]==2)
sa=0.55;
else if((spawn_lane[ID]==0 && dest_lane[ID]==1) || (spawn_lane[ID]==1 && dest_lane[ID]==2) || spawn_lane[ID]==2 && dest_lane[ID]==3 || spawn_lane[ID]==3 && dest_lane[ID]==0)
sa=-1.3;
}

} 


return sa;
}

void adjustpositionafterturning(int id){

if(orientation[id] == final_orientation[id])
return;

if(no_of_lanes==3){
if(dest_lane[id]==0 && orientation[id] > PI/2){
positionUL[id][0]=positionLL[id][0];
positionUR[id][0]=positionLR[id][0];
orientation[id]=PI/2;
}
else if(dest_lane[id]==2 && orientation[id] < PI/2){
positionUL[id][0]=positionLL[id][0];
positionUR[id][0]=positionLR[id][0];
orientation[id]=PI/2;
}
else if(dest_lane[id]==3 && orientation[id] > 0.0 &&  orientation[id] < PI/2){
positionUL[id][1]=positionLL[id][1];
positionUR[id][1]=positionLR[id][1];
orientation[id]=0.0;
}
else if(dest_lane[id]==5 && orientation[id] < 2*PI && orientation[id] > 3*PI/2 ){
positionUL[id][1]=positionLL[id][1];
positionUR[id][1]=positionLR[id][1];
orientation[id]=0.0;
}
else if(dest_lane[id]==6 && orientation[id] > 3*PI/2){
positionUL[id][0]=positionLL[id][0];
positionUR[id][0]=positionLR[id][0]; 
orientation[id]=3*PI/2;
}
else if(dest_lane[id]==8 && orientation[id] < 3*PI/2 && orientation[id]> PI){
positionUL[id][0]=positionLL[id][0];
positionUR[id][0]=positionLR[id][0];                                     
orientation[id]=3*PI/2;
}
else if(dest_lane[id]==9 && orientation[id] > PI){
positionUL[id][1]=positionLL[id][1];
positionUR[id][1]=positionLR[id][1];
orientation[id]=PI;
}
else if(dest_lane[id]==11 && orientation[id] < PI){
positionUL[id][1]=positionLL[id][1];
positionUR[id][1]=positionLR[id][1];
orientation[id]=PI;
}

}else if(no_of_lanes==2){
if(dest_lane[id]==0 && orientation[id] > PI/2){
positionUL[id][0]=positionLL[id][0];
positionUR[id][0]=positionLR[id][0];
orientation[id]=PI/2;
}
else if(dest_lane[id]==1 && orientation[id] < PI/2){
positionUL[id][0]=positionLL[id][0];
positionUR[id][0]=positionLR[id][0];
orientation[id]=PI/2;
}
else if(dest_lane[id]==2 && orientation[id] > 0.0 &&  orientation[id] < PI/2){
positionUL[id][1]=positionLL[id][1];
positionUR[id][1]=positionLR[id][1];
orientation[id]=0.0;
}
else if(dest_lane[id]==3 && orientation[id] < 2*PI && orientation[id] > 3*PI/2 ){
positionUL[id][1]=positionLL[id][1];
positionUR[id][1]=positionLR[id][1];
orientation[id]=0.0;
}
else if(dest_lane[id]==4 && orientation[id] > 3*PI/2){
positionUL[id][0]=positionLL[id][0];
positionUR[id][0]=positionLR[id][0]; 
orientation[id]=3*PI/2;
}
else if(dest_lane[id]==5 && orientation[id] < 3*PI/2 && orientation[id]> PI){
positionUL[id][0]=positionLL[id][0];
positionUR[id][0]=positionLR[id][0]; 
orientation[id]=3*PI/2;
}
else if(dest_lane[id]==6 && orientation[id] > PI){
positionUL[id][1]=positionLL[id][1];
positionUR[id][1]=positionLR[id][1];
orientation[id]=PI;
}
else if(dest_lane[id]==7 && orientation[id] < PI){
positionUL[id][1]=positionLL[id][1];
positionUR[id][1]=positionLR[id][1];
orientation[id]=PI;
}

}else if(no_of_lanes==1){
if((dest_lane[id]==0 && spawn_lane[id]==1 && orientation[id] > PI/2) || (dest_lane[id]==0 && spawn_lane[id]==3 && orientation[id] < PI/2)){
positionUL[id][0]=positionLL[id][0];
positionUR[id][0]=positionLR[id][0];
orientation[id]=PI/2;
}
else if((dest_lane[id]==1 && spawn_lane[id]==2 && orientation[id] > 0.0 &&  orientation[id] < PI/2) || (dest_lane[id]==1 && spawn_lane[id]==0 && orientation[id] < 2*PI && orientation[id] > 3*PI/2) ){
positionUL[id][1]=positionLL[id][1];
positionUR[id][1]=positionLR[id][1];
orientation[id]=0.0;
}
else if((dest_lane[id]==2 && spawn_lane[id]==3 && orientation[id] > 3*PI/2) || (dest_lane[id]==2 && spawn_lane[id]==1 && orientation[id] < 3*PI/2 && orientation[id]> PI) ){
positionUL[id][0]=positionLL[id][0];
positionUR[id][0]=positionLR[id][0];
orientation[id]=3*PI/2;
}
else if((dest_lane[id]==3 && spawn_lane[id]==0 && orientation[id] > PI) || ( dest_lane[id]==3 && spawn_lane[id]==2 && orientation[id] < PI)){

positionUL[id][1]=positionLL[id][1];
positionUR[id][1]=positionLR[id][1];
orientation[id]=PI;
}

}

}

void move(int id){

// Make sure the vehicle will not go in reverse(backward) direction or go over the speed limit
if(velocity[id] <= 0.0 && acceleration[id]< 0.0) {acceleration[id]=0.0; velocity[id]=0.0;}
else if(velocity[id] >= speed_limit && acceleration[id]> 0.0) {acceleration[id]=0.0; velocity[id]=speed_limit;}

// update velocity and distance covered by the vehicle in a single time step according to Newtonian kinematics for uniform acceleration
velocity[id]= velocity[id] + acceleration[id] * step; // v=u+at 
distance[id]= velocity[id] * step - 0.5 * acceleration[id] * step * step; // d=vt-0.5at^2 

total_distance_traveled[id]=total_distance_traveled[id] + distance[id];
total_journey_time[id]=total_journey_time[id] + step;
//get steeringangle
steeringangle[id]=getsteeringangle(steeringangle[id],id);

//update vehicle boundary points(UL,UR,LL and LR), calculate some distance parameters and update vehicle's crossing state.

steering(steeringangle[id],distance[id], vehicle_length,positionLM[id][0],positionLM[id][1],orientation[id],id);

//adjust the position of the vehicle before reaching the final heading
//TODO: calculate a new steering angle for the final turn step so that vehicle reaches the intended position
// beta=sin_inverse((X-Xold)/Radius + sin(heading))-heading;
// steeringangle_final=tan_inverse(beta * vehicle_length / distance);
adjustpositionafterturning(id);
//}

positionLR[id][0]=positionLM[id][0] +(vehicle_width/2)*sin(orientation[id]);
positionLR[id][1]=positionLM[id][1] -(vehicle_width/2)*cos(orientation[id]);

positionLL[id][0]=positionLM[id][0] -(vehicle_width/2)*sin(orientation[id]);
positionLL[id][1]=positionLM[id][1] +(vehicle_width/2)*cos(orientation[id]);

positionUL[id][0]=positionLL[id][0]+vehicle_length*cos(orientation[id]);
positionUL[id][1]=positionLL[id][1]+vehicle_length*sin(orientation[id]);

positionUR[id][0]=positionLR[id][0]+vehicle_length*cos(orientation[id]);
positionUR[id][1]=positionLR[id][1]+vehicle_length*sin(orientation[id]);

// another position point used in calculating distance between vehicle infront
positionUM[id][0]=positionLM[id][0]+vehicle_length*cos(orientation[id]);
positionUM[id][1]=positionLM[id][1]+vehicle_length*sin(orientation[id]);


if(current_road[id]==0){
distance_to_intersection[id] = road_length - positionUL[id][1]; 
//distance_to_vehicle_infront[id]=positionLL[vehicle_infront_id[id]][1] - positionUL[id][1]; 
}else if(current_road[id]==1){
distance_to_intersection[id] = road_length - positionUL[id][0]; 
//distance_to_vehicle_infront[id]=positionLL[vehicle_infront_id[id]][0] - positionUL[id][0]; 
}else if(current_road[id]==2){
distance_to_intersection[id] = positionUL[id][1] - road_length - intersection_length; 
//distance_to_vehicle_infront[id]= positionUL[id][1] - positionLL[vehicle_infront_id[id]][1]; 
}else if(current_road[id]==3){
distance_to_intersection[id] = positionUL[id][0] - road_length - intersection_length; 
//distance_to_vehicle_infront[id]= positionUL[id][0] - positionLL[vehicle_infront_id[id]][0] ; 
} 

distance_to_vehicle_infront[id]= pow((pow(positionLL[vehicle_infront_id[id]][0] - positionUL[id][0],2) + pow(positionLL[vehicle_infront_id[id]][1] - positionUL[id][1],2)), 0.5) ; 
//distance_to_vehicle_infront[id]= pow((pow(positionLM[vehicle_infront_id[id]][0] - positionUM[id][0],2) + pow(positionLM[vehicle_infront_id[id]][1] - positionUM[id][1],2)), 0.5) ; 
//distance_to_vehicle_infront[id]= pow((pow(positionLL[vehicle_infront_id[id]][0] - positionUR[id][0],2) + pow(positionLL[vehicle_infront_id[id]][1] - positionUR[id][1],2)), 0.5) ; 

// calculate the perpendicular distance to the end of journey(perpindicular distance from positionLL point on the vehicle and the positionLL point of the vehicle at the end of its journey , just before vehicle is terminated)
if(dest_road[id]==0)
perpendicular_distance_to_end_of_journey[id]=    2*road_length+intersection_length - positionLL[id][1];   
else if(dest_road[id]==1)
perpendicular_distance_to_end_of_journey[id]=    2*road_length+intersection_length - positionLL[id][0];  
else if(dest_road[id]==2)
perpendicular_distance_to_end_of_journey[id]=    positionLL[id][1] - 0.0;  
else if (dest_road[id]==3)
perpendicular_distance_to_end_of_journey[id]=    positionLL[id][0] - 0.0;  

// calculate the distance required to get a vehicle to a complete stop 
distance_to_come_to_complete_stop[id]= - 0.5 * vehicle_maxDeceleration * (velocity[id]/vehicle_maxDeceleration) * (velocity[id]/vehicle_maxDeceleration); // d=vt-0.5at^2 , t=(v-u)/a where v=0  

//update vehicle state
if( crossing_state[id]==0 && distance_to_intersection[id]< 0.0) // if the vehicle's front has crossed the intersection enterance line, then it is in crossing state 1 (crossing)
crossing_state[id]=1; //vehicle has already left its spawn road and is crossing (or has crossed) the intersection

if( crossing_state[id]==1 && perpendicular_distance_to_end_of_journey[id]< road_length ){ // if the vehicle has completely exited the intersection (vehicle's back has crossed the intersection exit line), then it is in crossing state 2 (vehicle has already crossed the intersection)
crossing_state[id]=2;
no_of_vehicles_crossed++; // increment the counter for the number of vehicles which have crossed the intersection
}
//update vehicle's current road and lane and vehicle infront 
if(crossing_state[id]==1 && once[id]==false){
current_road[id]=dest_road[id];
current_lane[id]=dest_lane[id];
vehicle_infront_id[id]=last_vehicle_on_dest_lane_id[dest_lane[id]]; // make sure name is correct
last_vehicle_on_dest_lane_id[dest_lane[id]]=id; // added it to global variables.
once[id]=true;
}

//update vehicle infront
//vehicle_infront_id[id]=last_vehicle_on_lane_id[current_lane[id]];    
//last_vehicle_on_lane_id[current_lane[id]]=id;
}

// check if a vehicle is at the beginning of its spawn road
bool vehicleatstartofroad(int id){

double distancetocompletestop=-0.5 * vehicle_maxDeceleration * (speed_limit/vehicle_maxDeceleration)*(speed_limit/vehicle_maxDeceleration);  // d=vt-0.5at^2 , t=(v-u)/a where v=0,u=speed_limit 
double distnotconsforcol = distancetocompletestop + 1.0; // adding safety distance

if(spawn_road[id]==0){
if(positionUL[id][1]<= distnotconsforcol){ 
if(pilledup==false) id_of_first_vehicle_pilling_up=id;
pilledup=true ; return true;
}
}else if(spawn_road[id]==1){
if(positionUL[id][0]<= distnotconsforcol){ 
if(pilledup==false) id_of_first_vehicle_pilling_up=id;
pilledup=true ; return true;
}
}else if(spawn_road[id]==2){
if(positionUL[id][1] >= 2*road_length + intersection_length - distnotconsforcol){ 
if(pilledup==false) id_of_first_vehicle_pilling_up=id;
pilledup=true ; return true;
}
}else if(spawn_road[id]==3){
if(positionUL[id][0] >= 2*road_length + intersection_length - distnotconsforcol){ 
if(pilledup==false) id_of_first_vehicle_pilling_up=id;
pilledup=true ; return true;
}
}

return false;
}


bool checkIfCollisionIsNotRecordedBeforeAndRecordItIfNot(int id1,int id2)
{
int k;
for(k=0; k<no_of_collisions; k++){
if((collidedvehicles.id1[k]==id1 && collidedvehicles.id2[k]==id2) || (collidedvehicles.id1[k]==id2 && collidedvehicles.id2[k]==id1))
return false;
}
// Don't count vehicles that were spawned while there is/are vehicle(s) at the start of the road (this means vehicles have pilled up till the spawn point)
if(vehicleatstartofroad(id1))
return false;
if(vehicleatstartofroad(id2))
return false;
 
// otherwise add the vehicles to the struct
collidedvehicles.id1[no_of_collisions]=id1;
collidedvehicles.id2[no_of_collisions]=id2;
if(spawn_lane[id1]!=spawn_lane[id2]){
nonsamelanecollidedvehicles.id1[non_same_lane_collision_counter]=id1;
nonsamelanecollidedvehicles.id2[non_same_lane_collision_counter]=id2;
non_same_lane_collision_counter++;
}
return true;
}


// src: W. Randolph Franklin (WRF) PNPOLY - Point Inclusion in Polygon Test : http://www.ecse.rpi.edu/Homepages/wrf/Research/Short_Notes/pnpoly.html
int pnpoly(int nvert, double vertx[4], double verty[4], double testx, double testy){

//nvert 	Number of vertices in the polygon.
//vertx, verty 	Arrays containing the x- and y-coordinates of the polygon's vertices.
//testx, testy	X- and y-coordinate of the test point. 

  int i, j, c = 0;

  for (i = 0, j = nvert-1; i < nvert; j = i++) {

    if ( ((verty[i]>testy) != (verty[j]>testy)) &&

	 (testx < (vertx[j]-vertx[i]) * (testy-verty[i]) / (verty[j]-verty[i]) + vertx[i]) )

       c = !c;
  }

  return c;
}

// we check collisions through testing if any of the first vehicle's edge(corner) points are found to be inside the polygon of the second vehicle, 
// if that was false , we do another test this time by testing if any of the second vehicle's edge(corner) points are found to be inside the polygon of the first vehicle
// We use 'W. Randolph Franklin (WRF) PNPOLY - Point Inclusion in Polygon Test' (http://www.ecse.rpi.edu/Homepages/wrf/Research/Short_Notes/pnpoly.html)
//  C implementation of Jordan curve theorem to count the number of edges of the polygon that will be crossed by a ray coming out of the test point,
// if the count was an odd number then the point intersects the polygon, otherwise it does not.     
bool checkForCollision(int id1,int id2){

bool collision_exists = false;
double vert1x[4] = {positionUL[id1][0], positionUR[id1][0], positionLL[id1][0],positionLR[id1][0]}; // Array containing the x-coordinates of the first vehicle vertices.
double vert1y[4] = {positionUL[id1][1], positionUR[id1][1], positionLL[id1][1],positionLR[id1][1]}; // Array containing the y-coordinates of the first vehicle vertices.
double vert2x[4] = {positionUL[id2][0], positionUR[id2][0], positionLL[id2][0],positionLR[id2][0]}; // Array containing the x-coordinates of the second vehicle vertices.
double vert2y[4] = {positionUL[id2][1], positionUR[id2][1], positionLL[id2][1],positionLR[id2][1]}; // Array containing the y-coordinates of the second vehicle vertices.

if(pnpoly(4,vert2x,vert2y,positionUL[id1][0],positionUL[id1][1])==1)
collision_exists=true;
else if(pnpoly(4,vert2x,vert2y,positionUR[id1][0],positionUR[id1][1])==1)
collision_exists=true;
else if(pnpoly(4,vert2x,vert2y,positionLL[id1][0],positionLL[id1][1])==1)
collision_exists=true;
else if(pnpoly(4,vert2x,vert2y,positionLR[id1][0],positionLR[id1][1])==1)
collision_exists=true;
else if(pnpoly(4,vert1x,vert1y,positionUL[id2][0],positionUL[id2][1])==1)
collision_exists=true;
else if(pnpoly(4,vert1x,vert1y,positionUR[id2][0],positionUR[id2][1])==1)
collision_exists=true;
else if(pnpoly(4,vert1x,vert1y,positionLL[id2][0],positionLL[id2][1])==1)
collision_exists=true;
else if(pnpoly(4,vert1x,vert1y,positionLR[id2][0],positionLR[id2][1])==1)
collision_exists=true;

if(collision_exists ==false)
return false;
else if(checkIfCollisionIsNotRecordedBeforeAndRecordItIfNot(id1,id2))
return true;
else
return false;
}

void checkCollisions(){
int i;
int j;
for(i=1; i<vehicleID; i++){
for(j=i+1; j<vehicleID; j++){
if(terminatedvehicleID[i]==false && terminatedvehicleID[j]==false){ // consider vehicles which are not terminated (are still moving)  
if(checkForCollision(i,j)){ 
no_of_collisions++;
if (crossing_state[i]==1 && crossing_state[j]==1)  
no_of_collisions_on_intersection++; // no of collisions happening within the intersection area
}
}
}
}
}



// check if vehicles have reached the end of their journey , this is used to triger the exit() internal (uppaal) function which terminates the dynamically created vehicles
bool endOfJourney(int id){
if(current_road[id]==0){
// for all lanes on the north road when the y coordinate of the lowerpoint(LL or LR) in the vehicle cross the road end point (2*road_length+intersection_length) the dynamically created vehicle is removed  
if(positionLL[id][1] > 2*road_length+intersection_length)
return true;
} else if(current_road[id]==1){
// for all lanes on the east road when the x coordinate of the lowerpoint(LL or LR) in the vehicle cross the road end point (2*road_length+intersection_length) the dynamically created vehicle is removed  
if(positionLL[id][0] > 2*road_length+intersection_length)
return true;
} else if(current_road[id]==2){
// for all lanes on the south road when the x coordinate of the lowerpoint(LL or LR) in the vehicle cross the road end point (2*road_length+intersection_length) the dynamically created vehicle is removed  
if(positionLL[id][1] < 0.0)
return true;
} else if (current_road[id]==3){
// for all lanes on the west road when the x coordinate of the lowerpoint(LL or LR) in the vehicle cross the road end point (2*road_length+intersection_length) the dynamically created vehicle is removed  
if(positionLL[id][0] < 0.0)
return true;
} 

return false;
}
// terminate the vehicle and calculate the average trip delay
void terminatevehicle(int id){
terminatedvehicleID[id]=true;
positionUL[id][0]=0.0;
positionUL[id][1]=0.0;
positionUR[id][0]=0.0;
positionUR[id][1]=0.0; 
positionLL[id][0]=0.0;
positionLL[id][1]=0.0; 
positionLR[id][0]=0.0; 
positionLR[id][1]=0.0; 
positionLM[id][0]=0.0;
positionLM[id][1]=0.0;

no_of_cleared_vehicles++; // increment the counter for the number of vehicles which have finished their journey

// get the average trip delay : average = old_average * (no-1)/no + new_value /no
average_trip_delay=average_trip_delay* (no_of_cleared_vehicles-1)/no_of_cleared_vehicles + (total_journey_time[id]-(total_distance_traveled[id]/speed_limit))/no_of_cleared_vehicles ;
}
